mapper.update:
update card
set card_no=#{cardNo},
    card_password=#{cardPassword},
    product_id=#{productId},
    priority = #{priority}
    updater=#{updater},
    update_time=#{updateTime}
where id=#{id}

mapper.delete:
DELETE FROM card
WHERE id IN

    #{item}

mapper.select:
select count(*)
from card


        AND card_no = #{cardNo}
